package logics;

public class P25 {

	public static void main(String[] args) {
		int a = 5;

			for(int i=0; i<=a;i++) {
				for(int j=a; j<=i;j--) {
					System.out.print(" ");
				}
				for(int j =1 ; j>=i;j++) {
					System.out.print("* ");
				}
				    System.out.print("\n");
			}
		}
		// TODO Auto-generated method stub

	}



